import React, { Component } from 'react';
import axios from 'axios';
import BlogList from './BlogList';

class Blog extends Component{
    state = {
        "info" : 
            [
                {
                    "title" : "test" ,
                    "contents" : "test",
                    "create_date" : "2019-06-10"
                }
            ]
        }

    async componentDidMount() {
        let listData;
        listData = await axios.get("http://localhost:8080/");
        this.setState({
            info : listData.data.rows
        })
        console.log("this state info : :" + JSON.stringify(this.state.info));
    }

    handleClick = () => {
        console.log("click!")
    }

    render() {
        return (
            <div>
                <BlogList onClick={this.handleClick} info={this.state.info}/>
            </div>
        )
    }
}

export default Blog;
